import { useEffect, useState } from "react";
import Header from "../components/Header";
import TradeForm from "../components/TradeForm";
import TradeList from "../components/TradeList";
import { fetchHello, fetchTime, fetchTrades } from "../api/api";

export default function Dashboard() {
  const [message, setMessage] = useState("Loading...");
  const [time, setTime] = useState("Loading...");
  const [trades, setTrades] = useState([]);

  // Finnhub REST price
  const [symbol, setSymbol] = useState("");
  const [priceData, setPriceData] = useState(null);

  // Live WebSocket price
  const [liveSymbol, setLiveSymbol] = useState("");
  const [livePrice, setLivePrice] = useState(null);
  const [wsError, setWsError] = useState("");

  // Load backend info + trades
  useEffect(() => {
    fetchHello().then((d) => setMessage(d.message));
    fetchTime().then((d) => setTime(d.server_time));
    fetchTrades().then((d) => setTrades(d));
  }, []);

  // Fetch price via REST
  const getMarketPrice = async () => {
    if (!symbol) return;

    const res = await fetch(`/api/price?symbol=${symbol}`);
    const data = await res.json();
    setPriceData(data);
  };

  // --- LIVE WEBSOCKET STREAM ---
  useEffect(() => {
    if (!liveSymbol) return;

    // Connect to backend WebSocket
    let ws = new WebSocket(`ws://localhost:8080/api/live?symbol=${liveSymbol}`);

    ws.onopen = () => {
      setWsError("");
      console.log("WS connected for", liveSymbol);
    };

    ws.onmessage = (event) => {
      try {
        const data = JSON.parse(event.data);

        if (data.type === "trade" && data.data && data.data.length > 0) {
          setLivePrice(data.data[0].p);
        }
      } catch (e) {
        console.error("WS parse error", e);
      }
    };

    ws.onerror = (err) => {
      setWsError("WebSocket error ‚Äî unsupported symbol or API key invalid");
      console.error("WebSocket error:", err);
    };

    ws.onclose = () => {
      console.warn("WebSocket closed");
    };

    return () => ws.close();
  }, [liveSymbol]);

  return (
    <div style={{ padding: 20 }}>
      <Header />

      {/* Backend info */}
      <p>{message}</p>
      <p>Server Time: {time}</p>

      <hr />

      {/* Finnhub REST price */}
      <h2>üîç Get Market Price (REST API)</h2>

      <input
        type="text"
        placeholder="AAPL, TSLA, BINANCE:BTCUSDT, ^NSEI"
        value={symbol}
        onChange={(e) => setSymbol(e.target.value)}
        style={{ marginRight: 10 }}
      />
      <button onClick={getMarketPrice}>Get Price</button>

      {priceData && (
        <pre style={{ marginTop: 20, padding: 15, background: "#eee" }}>
          {JSON.stringify(priceData, null, 2)}
        </pre>
      )}

      <hr />

      {/* LIVE WebSocket price */}
      <h2>üì° Live Streaming Price (WebSocket)</h2>

      <input
        type="text"
        placeholder="AAPL, TSLA, BINANCE:BTCUSDT"
        value={liveSymbol}
        onChange={(e) => setLiveSymbol(e.target.value)}
        style={{ marginRight: 10 }}
      />

      {wsError && <p style={{ color: "red" }}>{wsError}</p>}

      {livePrice && (
        <h3 style={{ marginTop: 10 }}>
          Live Price for {liveSymbol}: {livePrice}
        </h3>
      )}

      <hr />

      {/* Trade Form & List */}
      <TradeForm setTrades={setTrades} />
      <TradeList trades={trades} />
    </div>
  );
}
