import { useEffect, useState } from "react";
import Header from "../components/Header";
import TradeForm from "../components/TradeForm";
import TradeList from "../components/TradeList";
import { fetchHello, fetchTime, fetchTrades } from "../api/api";

// Live WebSocket component
function LivePrice({ symbol }) {
  const [livePrice, setLivePrice] = useState(null);

  useEffect(() => {
    if (!symbol) return;

    const ws = new WebSocket(`ws://localhost:8080/api/live?symbol=${symbol}`);

    ws.onmessage = (msg) => {
      try {
        const data = JSON.parse(msg.data);
        if (data.type === "trade" && data.data && data.data.length > 0) {
          setLivePrice(data.data[0].p); // live price from Finnhub
        }
      } catch (err) {
        console.error("WebSocket error:", err);
      }
    };

    return () => ws.close();
  }, [symbol]);

  return (
    <div style={{ marginTop: 10 }}>
      <h3>üì° Live Price ({symbol}): {livePrice ?? "Waiting..."}</h3>
    </div>
  );
}

export default function Dashboard() {
  const [message, setMessage] = useState("Loading...");
  const [time, setTime] = useState("Loading...");
  const [trades, setTrades] = useState([]);

  // For REST market price
  const [symbol, setSymbol] = useState("");
  const [priceData, setPriceData] = useState(null);

  // Load backend info + trades
  useEffect(() => {
    fetchHello().then((d) => setMessage(d.message));
    fetchTime().then((d) => setTime(d.server_time));
    fetchTrades().then((d) => setTrades(d));
  }, []);

  // Call backend REST price API
  const getMarketPrice = async () => {
    if (!symbol) return alert("Enter a symbol first");

    const res = await fetch(`/api/price?symbol=${symbol}`);
    const data = await res.json();
    setPriceData(data);
  };

  return (
    <div style={{ padding: 20 }}>
      <Header />

      {/* Backend info */}
      <p>{message}</p>
      <p>üïí Server Time: {time}</p>

      <hr />

      {/* Market Price via REST */}
      <h2>üîç Check Market Price</h2>

      <input
        type="text"
        placeholder="AAPL, TSLA, BINANCE:BTCUSDT, OANDA:USD_INR"
        value={symbol}
        onChange={(e) => setSymbol(e.target.value)}
        style={{ padding: 5, width: 250, marginRight: 10 }}
      />
      <button onClick={getMarketPrice}>Get Price</button>

      {priceData && (
        <pre
          style={{
            marginTop: 20,
            padding: 15,
            background: "#eeeeee",
            borderRadius: 5,
          }}
        >
          {JSON.stringify(priceData, null, 2)}
        </pre>
      )}

      <hr />

      {/* Live WebSocket Price */}
      {symbol && (
        <>
          <h2>üì° Live Price Stream</h2>
          <LivePrice symbol={symbol} />
        </>
      )}

      <hr />

      {/* Add Trade Form */}
      <TradeForm setTrades={setTrades} />

      {/* Trades List */}
      <TradeList trades={trades} />
    </div>
  );
}
