import { useEffect, useState } from "react";
import Header from "../components/Header";
import TradeForm from "../components/TradeForm";
import TradeList from "../components/TradeList";
import { fetchHello, fetchTime, fetchTrades } from "../api/api";

export default function Dashboard() {
  const [message, setMessage] = useState("Loading...");
  const [time, setTime] = useState("Loading...");
  const [trades, setTrades] = useState([]);

  // LIVE PRICE
  const [liveSymbol, setLiveSymbol] = useState("AAPL");
  const [livePrice, setLivePrice] = useState(null);

  useEffect(() => {
    fetchHello().then((d) => setMessage(d.message));
    fetchTime().then((d) => setTime(d.server_time));
    fetchTrades().then((d) => setTrades(d));
  }, []);

  // Live WebSocket
  useEffect(() => {
    if (!liveSymbol) return;

    const ws = new WebSocket(
      `ws://localhost:8080/api/live?symbol=${liveSymbol}`
    );

    ws.onmessage = (msg) => {
      try {
        const data = JSON.parse(msg.data);
        if (data.data && data.data.length > 0) {
          setLivePrice(data.data[0].p); // price field from finnhub
        }
      } catch (e) {
        console.log("parse error:", e);
      }
    };

    ws.onerror = (e) => console.log("WS ERR", e);
    ws.onclose = () => console.log("WS CLOSED");

    return () => ws.close();
  }, [liveSymbol]);

  return (
    <div style={{ padding: 20 }}>
      <Header />
      <p>{message}</p>
      <p>Server Time: {time}</p>

      <hr />

      <h2>Live Price Streaming (Finnhub WebSocket)</h2>
      <input
        value={liveSymbol}
        onChange={(e) => setLiveSymbol(e.target.value.toUpperCase())}
        placeholder="AAPL"
        style={{ marginRight: 10 }}
      />
      <strong>Live Price: {livePrice ?? "Waiting..."}</strong>

      <hr />

      <TradeForm setTrades={setTrades} />
      <TradeList trades={trades} />
    </div>
  );
}
