import { useState, useEffect } from "react";

export default function LiveStreamer() {
  const [symbol, setSymbol] = useState("");
  const [livePrice, setLivePrice] = useState(null);
  const [wsInstance, setWsInstance] = useState(null);

  const startStream = () => {
    if (!symbol) return;

    if (wsInstance) {
      wsInstance.close();
    }

    const ws = new WebSocket(`ws://localhost:8080/api/live?symbol=${symbol}`);
    setWsInstance(ws);

    ws.onmessage = (msg) => {
      const data = JSON.parse(msg.data);

      if (data.type === "trade" && data.data?.length > 0) {
        setLivePrice(data.data[0].p);
      }
    };

    ws.onerror = () => console.log("WebSocket error");
    ws.onclose = () => console.log("WebSocket closed");
  };

  return (
    <div>
      <h2>ðŸ“¡ Live Price Stream</h2>
      <input
        type="text"
        placeholder="e.g., AAPL, TSLA, BINANCE:BTCUSDT"
        value={symbol}
        onChange={(e) => setSymbol(e.target.value)}
      />
      <button onClick={startStream}>Start Streaming</button>

      {livePrice && (
        <h3 style={{ marginTop: 15 }}>Live Price: {livePrice}</h3>
      )}
    </div>
  );
}
